import { uiActions } from "./ui-slice";

export const fetchCartData = () => {
  return (dispatch) => {
    const fetchData = async () => {
      const response = await fetch(
        "https://redux-practice-496e0-default-rtdb.firebaseio.com/cart.json"
      );
      if (!response.ok) {
      }
      const data = await response.json();
    };
  };
};

// will dispatch this sendcartdata action

export const sendCartData = (cart) => {
  // separate js standalone function
  // before calling the dispatch we can perform any asynchronous code or sideeffect because we will have not reached our reducer yet
  return async (dispatch) => {
    dispatch(
      uiActions.showNotification({
        status: "pending",
        title: "Sending...",
        message: "Sending cart data!",
      })
    );
    const sendRequest = async () => {
      const response = await fetch(
        "https://redux-practice-496e0-default-rtdb.firebaseio.com/cart.json",
        {
          method: "PUT",
          body: JSON.stringify(cart),
        }
      );
      if (!response.ok) {
        throw new Error("Sending cart data failed");
      }
    };

    try {
      await sendRequest();
      dispatch(
        uiActions.showNotification({
          status: "success",
          title: "Success!",
          message: "Sent cart data successfully!",
        })
      );
    } catch (error) {
      dispatch(
        uiActions.showNotification({
          status: "error",
          title: "Error!",
          message: "Sent cart data failed!",
        })
      );
    }
  };
};
